
tinymce.PluginManager.add('previewcontent', function(editor, url) {
    var contentUrl = editor.settings.contentUrl;
    // Add a button that opens a window
    editor.addButton('previewcontent', {
        icon: 'preview',
        tooltip:"预览",
        onclick: function() {
            layer.open({
                type: 2
                , title: false
                , shadeClose: true
                , scrollbar: false
                , area: ['350px', '500px']
                , content: contentUrl //这里content是一个URL，如果你不想让iframe出现滚动条，你还可以content: ['http://sentsin.com', 'no']
            });
        }
    });

});